import React, { Component } from 'react'
import {
  FaCheckCircle,
  FaTrash,
  FaCommentsDollar
} from 'react-icons/fa'
import axios from 'axios'
import { Spinner, Modal } from 'reactstrap';
import Checkbox from './../Checkbox/Checkbox';
import InputForm from '../InputForm/InputForm';
import { ButtonOutline } from '../Button/Button'

const API_URL = process.env.REACT_APP_API_URL

const moralCopy = {
  addrres: '',
  activities: '',
  business_name: '',
  cap_reg: '',
  last_update: {
    0: '',
    1: '',
    2: ''
  },
  first_op: {
    0: '',
    1: '',
    2: ''
  },
  rfc: '',
  status: '',
  tradename: '',
  entities: []
}
const checkedCopy = [false, false, false, false, false]

class DocumentCard extends Component {

  constructor(props) {
    super(props)

    this.checkCard = this.checkCard.bind(this)

    this.inputRef = React.createRef();

    this.state = {
      image_event: '',
      checkEntities: true,
      image: '',
      image_open: false,
      spinner: 'd-none',
      doc_type: '',
      disabled: true,
      num_activ: '',
      doc_up: false,
      comp_id: 0,
      checked: [false, false, false, false, false],
      documentsList: [{
        image: '-ife',
        documentType: 'INE',
      }, {
        image: '-license',
        documentType: 'Licencia',
      }, {
        image: '-passport',
        documentType: 'Pasaporte'
      }, {
        image: '-acta',
        documentType: 'Acta',
      }, {
        image: '-rfc',
        documentType: 'RFC'
      }],
      file: '',
      pdf: false,
      info: false,
      // Data for physical person
      user: {
        name: '',
        first_surname: '',
        second_surname: '',
        city: '',
        colony: '',
        state: '',
        street: '',
        gender: '',
        clave: '',
        curp: '',
        birthdate: ''
